replace into item_template (entry, display_id, name, class, quality, flags, buy_count, allowable_class, allowable_race, item_level, stackable, spellcooldown_1, spellcategorycooldown_1, spellcooldown_2, spellcategorycooldown_2, bonding, description, page_text, material) values
(41196,7601,'Rommath\'s Orders',12,1,2048,1,-1,-1,1,1,-1,-1,-1,-1,4,'50692',0,7);
replace INTO page_text (`entry`, `text`, `next_page`) VALUES
(50692, 'Destroy this letter once you have memorized its contents.$B$BAll preparations have been made. Send your delegation to Alah\'Thalas at once. Eldin is not an option; he will be recognized. Choose one of your most trusted men, and ensure they cover their eyes during the journey. $B$BI will await your arrival until tomorrow. Should you be delayed or unable to reach Alah\'Thalas in time, I will contact him independently. I trust in the best outcome.$B$BRommath', 0);
